import unittest
import string
from cipher import isCharacter

class TestIsCharacter(unittest.TestCase):
    
    def test_IsCharacter_5d8094df9c(self):
        # Test with a valid lowercase character
        self.assertTrue(isCharacter('a'), "The function failed to identify a valid lowercase character.")
        
        # Test with a valid uppercase character
        self.assertTrue(isCharacter('A'), "The function failed to identify a valid uppercase character.")
        
        # Test with a digit character
        self.assertFalse(isCharacter('1'), "The function incorrectly identified a digit as a character.")
        
        # Test with a special character
        self.assertFalse(isCharacter('@'), "The function incorrectly identified a special character as a character.")
        
        # Test with an empty string
        self.assertFalse(isCharacter(''), "The function incorrectly identified an empty string as a character.")
        
        # Test with a string of multiple characters
        self.assertFalse(isCharacter('abc'), "The function incorrectly identified a string of multiple characters as a single character.")
        
        # Test with a whitespace character
        self.assertFalse(isCharacter(' '), "The function incorrectly identified a whitespace as a character.")
        
        # Test with every letter in the alphabet
        for letter in string.ascii_letters:
            with self.subTest(letter=letter):
                self.assertTrue(isCharacter(letter), f"The function failed to identify {letter} as a valid character.")
        
if __name__ == '__main__':
    unittest.main()
