import unittest
from string import ascii_lowercase as alphabet

def isCharacter(element):
    for letter in alphabet:
        if(element.lower() == letter):
            return True
    return False 

class TestIsCharacter(unittest.TestCase):
    def test_isCharacter(self):
        self.assertEqual(isCharacter('a'), True)
        self.assertEqual(isCharacter('z'), True)
        self.assertEqual(isCharacter('A'), True)
        self.assertEqual(isCharacter('Z'), True)
        self.assertEqual(isCharacter('1'), False)
        self.assertEqual(isCharacter('!'), False)
        self.assertEqual(isCharacter(' '), False)

if __name__ == '__main__':
    unittest.main()
